// src/components/Pacjent/PacjentDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../../api';

const PacjentDetail = () => {
  const { id } = useParams();
  const [pacjent, setPacjent] = useState(null);

  useEffect(() => {
    // Pobierz szczegóły pacjenta z API po załadowaniu komponentu
    api.get(`/pacjenci/${id}/`)
      .then(response => setPacjent(response.data))
      .catch(error => console.error('Błąd pobierania szczegółów pacjenta:', error));
  }, [id]);

  if (!pacjent) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Szczegóły Pacjenta</h2>
      <p>{pacjent.imie} {pacjent.nazwisko}</p>
      <p>PESEL: {pacjent.pesel}</p>
      {/* Dodaj inne dane pacjenta */}
    </div>
  );
};

export default PacjentDetail;
