import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rezerwacje = () => {
  const [pacjent, setPacjent] = useState({});
  const [loading, setLoading] = useState(true);
  const [userInfo, setUserInfo] = useState({}); // Dodaj zmienną userInfo


  useEffect(() => {
    const fetchData = async () => {
      try {
        const authToken = localStorage.getItem('authToken');

        if (authToken) {
          // Pobierz informacje o użytkowniku na podstawie tokena
          const response = await axios.get('http://localhost:8000/api/get_user_info/', {
            headers: {
              Authorization: `Token ${authToken}`,
            },
          });
          const userInfo = response.data;

          // Ustaw dane pacjenta
          setPacjent(userInfo.user_data);

          console.log(userInfo);
          console.log(userInfo.user_data);

          // Zakończ ładowanie
          setLoading(false);
        }
      } catch (error) {
        console.error('Błąd pobierania informacji o użytkowniku:', error);
      }
    };

    fetchData();
  }, []); // Ustaw puste zależności, aby useEffect wykonał się tylko raz po zamontowaniu komponentu

  if (loading) {
    return <p>Ładowanie danych...</p>;
  }

return (
  <>
    {pacjent && (
      <>
        <h1>Rezerwacja wizyty</h1>
        <p>Informacje o pacjencie:</p>
        <p>Imię: {pacjent.imie}</p>
        <p>Nazwisko: {pacjent.nazwisko}</p>
        <p>Email: {pacjent.email}</p>
        <p>PESEL: {pacjent.pesel}</p>
      </>
    )}
  </>
);

};

export default Rezerwacje;
