// RejestracjaForm.js
import React, { useState } from 'react';
import axios from 'axios';

const RejestracjaForm = () => {
  const [nowyPacjent, setNowyPacjent] = useState({
    imie: '',
    nazwisko: '',
    pesel: '',
    data_urodzenia: '',
    adres: '',
    email: '',
    haslo: '',
    inne_informacje: '', // Dodaj nowe pole
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNowyPacjent((prevPacjent) => ({ ...prevPacjent, [name]: value }));
  };

  const zarejestrujPacjenta = () => {
    axios
      .post('http://localhost:8000/api/rejestracja/', nowyPacjent)
      .then((response) => {
        console.log('Pacjent zarejestrowany:', response.data);
        // Tutaj możesz obsłużyć przekierowanie lub inne działania po udanej rejestracji
      })
      .catch((error) => {
        console.error('Błąd rejestracji pacjenta:', error);
        // Obsłuż błędy i wyświetl je użytkownikowi
      });
  };

  return (
    <div>
      <h2>Formularz Rejestracji</h2>
      <form>
        <label>
          Imię:
          <input
            type="text"
            name="imie"
            value={nowyPacjent.imie}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Nazwisko:
          <input
            type="text"
            name="nazwisko"
            value={nowyPacjent.nazwisko}
            onChange={handleInputChange}
          />
        </label>
        <label>
          PESEL:
          <input
            type="text"
            name="pesel"
            value={nowyPacjent.pesel}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Data Urodzenia:
          <input
            type="date"
            name="data_urodzenia"
            value={nowyPacjent.data_urodzenia}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Adres:
          <input
            type="text"
            name="adres"
            value={nowyPacjent.adres}
            onChange={handleInputChange}
          />
        </label>
        <label>
          E-mail:
          <input
            type="email"
            name="email"
            value={nowyPacjent.email}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Hasło:
          <input
            type="password"
            name="haslo"
            value={nowyPacjent.haslo}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Inne Informacje:
          <textarea
            name="inne_informacje"
            value={nowyPacjent.inne_informacje}
            onChange={handleInputChange}
          />
        </label>
        <button type="button" onClick={zarejestrujPacjenta}>
          Zarejestruj Pacjenta
        </button>
      </form>
    </div>
  );
};

export default RejestracjaForm;
