// src/components/Lekarz/LekarzDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../../api';

const LekarzDetail = () => {
  const { id } = useParams();
  const [lekarz, setLekarz] = useState(null);

  useEffect(() => {
    api.get(`/lekarze/${id}/`)
      .then(response => setLekarz(response.data))
      .catch(error => console.error('Błąd pobierania szczegółów lekarza:', error));
  }, [id]);

  if (!lekarz) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Szczegóły Lekarza</h2>
      <p>{lekarz.imie} {lekarz.nazwisko}</p>
      <p>Specjalizacja: {lekarz.specjalizacja}</p>
      {/* Dodaj inne dane lekarza */}
    </div>
  );
};

export default LekarzDetail;
