import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import NavigationBar from './NavigationBar'

const Rezerwacja = () => {
  const { lekarzId, termin } = useParams();
  const [userInfo, setUserInfo] = useState({});
  const [opisWizyty, setOpisWizyty] = useState('');

  useEffect(() => {
    // Pobierz informacje o zalogowanym użytkowniku
    axios.get('http://localhost:8000/api/get_user_info/')
      .then(response => {
        setUserInfo(response.data.user_data);
      })
      .catch(error => console.error('Błąd pobierania informacji o użytkowniku:', error));
  }, []);

  const handleZapiszWizyte = () => {
    // Wysyłamy zapytanie do API w celu zapisania wizyty
    axios.post('http://localhost:8000/api/zapisz-wizyte/', {
      lekarzId: lekarzId,
      termin: termin,
      pacjentId: userInfo.id,
      opis: opisWizyty,
    })
      .then(response => {
        // Obsługa sukcesu
        console.log('Wizyta została zapisana pomyślnie:', response.data);
        // Dodaj obsługę przekierowania użytkownika na inną stronę po zapisaniu wizyty
      })
      .catch(error => {
        // Obsługa błędu
        console.error('Błąd podczas zapisywania wizyty:', error);
        // Dodaj obsługę wyświetlania komunikatu o błędzie dla użytkownika
      });
  };

  return (
    <div>
      <NavigationBar />
      <h1>Rezerwacja wizyty</h1>
      <p>Imię lekarza: {/* Pobierz imię lekarza z odpowiednich danych */}</p>
      <p>Data i godzina wizyty: {new Date(termin).toLocaleString()}</p>
      <p>Imię i nazwisko pacjenta: {userInfo.first_name} {userInfo.last_name}</p>
      <p>Opis wizyty:</p>
      <textarea
        value={opisWizyty}
        onChange={(e) => setOpisWizyty(e.target.value)}
        rows={4}
        cols={50}
      />
      <button onClick={handleZapiszWizyte}>Zapisz wizytę</button>
    </div>
  );
};

export default Rezerwacja;
