import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { styled } from '@mui/system';
import { Link } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';

import Pacjent from './PatientPanel';
import Lekarz from './DoctorPanel';

const StyledFormContainer = styled('div')({
  width: '50%',
  margin: '0 auto',
  marginTop: '50px',
});

const StyledForm = styled('form')({
  display: 'flex',
  flexDirection: 'column',
  gap: '16px',
});

const StyledButton = styled(Button)({
  marginTop: '16px',
  backgroundColor: '#26a197',
});

const StyledAppBar = styled(AppBar)({
  backgroundColor: '#26a197',
});

const StyledToolbar = styled(Toolbar)({
  display: 'flex',
  justifyContent: 'space-between',
});

const StyledLogo = styled('img')({
  height: '50px', // Dostosuj do swoich wymagań
});

const StyledButtonsContainer = styled('div')({
  display: 'flex',
  gap: '16px',
});

const logo = 'link_do_twojego_logo.jpg'; // Podmień ścieżkę do swojego logo

const Login = () => {
  // ... (pozostała część kodu)

  return (
    <>
      <StyledAppBar position="static">
        <StyledToolbar>
          <StyledLogo src={logo} alt="PolwroMED Logo" />
          <Typography variant="h6">
            PolwroMED
          </Typography>
          <StyledButtonsContainer>
            {/* Użyj Link do nawigacji do nowej strony */}
            <Button color="inherit" component={Link} to="/login">
              Logowanie
            </Button>
            <Button color="inherit" component={Link} to="/rejestracja">
              Rejestracja
            </Button>
            <Button color="inherit" component={Link} to="/nasi-lekarze">
              Nasi lekarze
            </Button>
          </StyledButtonsContainer>
        </StyledToolbar>
      </StyledAppBar>

      <StyledFormContainer>
        {loggedIn ? (
          <div>
            <Typography variant="h5">Jesteś zalogowany</Typography>
            {userInfo.role === 'patient' && <Pacjent userData={userInfo.user_data} />}
            {userInfo.role === 'doctor' && <Lekarz userData={userInfo.user_data} />}
            <Button variant="contained" color="secondary" onClick={handleLogout}>
              Wyloguj
            </Button>
          </div>
        ) : (
          <div>
            <Typography variant="h5">Panel Logowania</Typography>
            <StyledForm>
              {/* Pozostała część formularza */}
            </StyledForm>
          </div>
        )}
      </StyledFormContainer>
    </>
  );
};

export default Login;
